		var audio_ = document.getElementById('audio');
		var index = 0;
		var data_music;
		//  进度数字增加
		var data = setInterval(function() {
			$("#num").html(index + "%");
			index++;
			if(index >= 50) {
				clearInterval(data);
				data = setInterval(function() {
					$("#num").html(index + "%");
					index++;
					if(index == 86) {
						index = 86;
						clearInterval(data);
						$("#num").html("100%");
						$(".loading").fadeOut("slow");
					}
				}, 1000);
			}
		}, 400);

		// 监听是否真正加载完毕
		var loadData = setInterval(function() {
			if(document.readyState == "complete") {
				$("#num").html("100%");
				$(".loading").fadeOut("slow");
				clearInterval(loadData);
				clearInterval(data);
				//  播放音乐
				data_music = setInterval(function() {
					audio_ = document.getElementById('audio');
					if(audio_.paused) {
						audio_.play();
						clearInterval(data_music);
					}
					//  如果暂停
					if(audio_.paused) {
						$("#music").removeClass("music");
						$("#music").attr("src", "img/c_music.png");
					} else {
						$("#music").addClass("music");
						$("#music").attr("src", "img/music.png");
					}
				}, 500);
			}
		}, 50);
		
		
	document.addEventListener("WeixinJSBridgeReady", function() {
		var audio_ = document.getElementById('audio');
		var loadData_wx = setInterval(function(){
			if(document.readyState == "complete") {
				clearInterval(data_music);
				clearInterval(loadData);
				clearInterval(data);
				clearInterval(loadData_wx);
				audio_.play();
				if(audio_.paused) {
					$("#music").removeClass("music");
					$("#music").attr("src", "img/c_music.png");
				} else {
					$("#music").addClass("music");
					$("#music").attr("src", "img/music.png");
				}
			}
		},50);
	});